package com.skyfilm.owner.pay;import android.text.TextUtils;public class PayResult {	private String resultStatus;	private String result;	private String memo;	private String no;	public PayResult(String rawResult) {		if (TextUtils.isEmpty(rawResult))			return;		String[] resultParams = rawResult.split(";");		for (String resultParam : resultParams) {			if (resultParam.startsWith("resultStatus")) {				resultStatus = gatValue(resultParam, "resultStatus");			}			if (resultParam.startsWith("result")) {				try {					result = gatValue(resultParam, "result");				} catch (Exception e) {				}			}			if (resultParam.startsWith("memo")) {				try {					memo = gatValue(resultParam, "memo");				} catch (Exception e) {				}			}		}	}	public String getNo() {		return no;	}	public void setNo(String no) {		this.no = no;	}	@Override	public String toString() {		return "resultStatus={" + resultStatus + "};memo={" + memo				+ "};result={" + result + "}";	}	private String gatValue(String content, String key) {		String prefix = key + "={";		return content.substring(content.indexOf(prefix) + prefix.length(),				content.lastIndexOf("}"));	}	/**	 * @return the resultStatus	 */	public String getResultStatus() {		return resultStatus;	}	/**	 * @return the memo	 */	public String getMemo() {		return memo;	}	/**	 * @return the result	 */	public String getResult() {		return result;	}}